(function() {
  if (Forma_Aquisicao.selectedOptionValue == 'Compra'){
	Valor_Compra.setVisibility(true)
	ProprietarioAnterior.setValue('')
	ProprietarioAnterior.setDisabled(false)
	} else if (Forma_Aquisicao.selectedOptionValue == 'Nascimento no Canil PMES'){
	ProprietarioAnterior.setDisabled(true)
	ProprietarioAnterior.setValue('Nascimento no Canil PMES')
	} else if (Forma_Aquisicao.selectedOptionValue == 'Nascimento em Canil externo'){
	ProprietarioAnterior.setDisabled(true)
